translations.javascript:
 var slideIndex = 0;
 var auto = true;
 var timeout = 3500;
showSlides(slideIndex);

var container = document.getElementById("slideshow-container-aboutus");

container.addEventListener("mouseover", mouseOver);
container.addEventListener("mouseout", mouseOut);
function mouseOver(){
   auto = false;
}
function mouseOut(){
   if (auto == false) {
   auto = true;
  }
}


// Next/previous controls
function plusSlides(n) {
  slideIndex = getCurrentIndex();
  showSlides(slideIndex += n);
}

// Thumbnail image controls
function currentSlide(n) {
  slideIndex = getCurrentIndex();
  showSlides(slideIndex = n);
}

function getCurrentIndex() {
   var slides = document.getElementsByClassName("mySlides");
   var current = Array.from(slides).find(s => s.style.display  == "block")
   var curIndex = Array.from(slides).indexOf(current)
   console.log("cur index is " + curIndex);
   return curIndex;
}

function showSlides(n) {
  var i;
  var slides = document.getElementsByClassName("mySlides");
  var dots = document.getElementsByClassName("dot");
  if (n >= slides.length) {slideIndex = 0}
  if (n < 0) {slideIndex = slides.length-1}
  for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
  }
  for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex].style.display = "block";
  dots[slideIndex].className += " active";
}

setInterval( () => { autoShowSlides() }, timeout);

function autoShowSlides() {
  if (!auto) {
      return;
   }  
  console.log("auto index "+ slideIndex );
  var i;
  var slides = document.getElementsByClassName("mySlides");
  var dots = document.getElementsByClassName("dot");
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";
  }
  for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
  }

  if (slideIndex >= slides.length) {slideIndex = 0}
  slides[slideIndex].style.display = "block";
  dots[slideIndex].className += " active";
  slideIndex++;
  // setTimeout(() => {
  //   console.log("call again");
  //  autoShowSlides();}, timeout); // Change image every 2 seconds
  //  }, 2000)
}
